      out.write("       var day = (now.getDate()) > 9 ? ''+(now.getDate()) : '0'+(now.getDate());           \r\n");
      out.write("       //년도 selectbox만들기               \r\n");
      out.write("       for(var i = 1920 ; i <= year ; i++) {\r\n");
