      out.write("       }\r\n");
      out.write("       $(\"#year  > option[value=\"+year+\"]\").attr(\"selected\", \"true\");        \r\n");
      out.write("       $(\"#month  > option[value=\"+mon+\"]\").attr(\"selected\", \"true\");    \r\n");
      out.write("       $(\"#day  > option[value=\"+day+\"]\").attr(\"selected\", \"true\");       \r\n");
      out.write("     \r\n");
      out.write("   })\r\n");
      out.write("   \r\n");
      out.write("   /* 주소 */\r\n");
      out.write("   \r\n");
      out.write("   /* 버튼을 클릭하여 execDaumPostcode() 함수가 호출되면서 주소 검색 팝업창이 보여지게 합니다.\r\n");
      out.write("        팝업창에서 주소 검색 결과 항목을 클릭했을 때 우편번호와 도로명주소 입력란에 값을 채워넣게 됩니다. */\r\n");
      out.write("   \r\n");
